javascript:!function(){var e,a,n,i,o,r;window.getSelection?window.getSelection().toString():document.getSelection().toString();alert("\n    Bienvenido al generador de prompts para ChatGPT.\n\n    Este asistente te guiará a través de varios pasos para crear un prompt efectivo y detallado.\n\n    A continuación, se te pedirá que selecciones un caso de uso y luego completarás varios campos para definir:\n\n    1. El rol de la IA.\n\n    2. La misión principal.\n\n    3. El contexto.\n\n    4. El objetivo.\n\n    5. Los requisitos adicionales.\n\n    6. El formato de salida.\n\n    \n\n    Por favor, sigue las instrucciones en cada pantalla para completar tu prompt.\n\n    ¡Comencemos!\n    ");for(var s=["Elaboración de informes","Revisión de escenarios analíticos","Preparación de estrategias","Ayuda en la toma de decisiones","Análisis de problemas","Formación continua","Formación de grado","Formación sanitaria especializada","Acreditación docente sanitaria","Gestión de equipos","Evaluación de desempeño","Optimización de recursos","Planificación de proyectos","Desarrollo profesional","Supervisión de programas","Gestión del cambio","Evaluación de necesidades","Comunicación interna","Gestión del conocimiento","Innovación en salud"],c="Seleccione un caso de uso:\n",l=0;l<s.length;l++)c+=l+1+": "+s[l]+"\n";switch(prompt(c,"1")){case"1":e="Eres un analista especializado en la creación de informes detallados y precisos",a="Elaborar un informe exhaustivo sobre un tema específico",n="Necesitas preparar un informe detallado para una reunión de alto nivel",i="Presentar datos y análisis relevantes de manera clara y concisa",o="Incluye estadísticas recientes, análisis comparativos y conclusiones basadas en datos",r="Informe estructurado con secciones claras y un resumen ejecutivo";break;case"2":e="Eres un experto en análisis de datos con habilidad para evaluar escenarios complejos",a="Revisar escenarios analíticos para apoyar la toma de decisiones estratégicas",n="Se requiere una revisión de varios escenarios para decidir el futuro de un proyecto importante",i="Proporcionar una evaluación detallada de cada escenario con recomendaciones claras",o="Incluye pros y contras, riesgos potenciales y análisis de impacto",r="Informe con tablas comparativas y gráficos explicativos";break;case"3":e="Eres un estratega experimentado en la formulación de planes a largo plazo",a="Desarrollar una estrategia integral para abordar un desafío específico",n="La organización necesita una estrategia para mejorar la eficiencia operativa",i="Crear un plan estratégico con objetivos claros y pasos detallados",o="Incluir análisis de fortalezas, debilidades, oportunidades y amenazas (SWOT)",r="Documento estratégico con objetivos, tácticas y cronograma de implementación";break;case"4":e="Eres un consultor de decisiones con experiencia en facilitar la toma de decisiones complejas",a="Asistir en la toma de decisiones críticas mediante análisis detallados",n="Se debe tomar una decisión importante sobre la asignación de recursos",i="Proporcionar una recomendación basada en análisis de datos y escenarios",o="Incluye análisis de costo-beneficio y posibles impactos a corto y largo plazo",r="Informe de decisión con opciones presentadas y justificación de la recomendación";break;case"5":e="Eres un experto en resolución de problemas con habilidades analíticas avanzadas",a="Analizar un problema complejo y proporcionar soluciones efectivas",n="La organización enfrenta un problema significativo que afecta su operativa",i="Identificar la raíz del problema y sugerir soluciones viables",o="Incluye análisis de causas, posibles soluciones y plan de implementación",r="Documento con análisis del problema, soluciones propuestas y plan de acción";break;case"6":e="Eres un formador con amplia experiencia en educación continua para profesionales de la salud",a="Desarrollar un programa de formación continua para el personal sanitario",n="Es necesario implementar un nuevo programa de formación para actualizar competencias",i="Diseñar un programa educativo que mejore las habilidades del personal",o="Incluye módulos de formación, objetivos de aprendizaje y métodos de evaluación",r="Programa de formación con cronograma, contenidos y materiales didácticos";break;case"7":e="Eres un coordinador académico especializado en la gestión de programas de grado en salud",a="Supervisar y mejorar un programa de grado en ciencias de la salud",n="El programa de grado necesita ser actualizado para cumplir con nuevos estándares",i="Asegurar que el programa cumple con los requisitos académicos y profesionales",o="Incluye revisión curricular, evaluación de docentes y feedback de estudiantes",r="Informe de evaluación del programa con recomendaciones de mejora";break;case"8":e="Eres un coordinador de formación sanitaria especializada con experiencia en programas de residencia",a="Gestionar la formación de residentes en un centro sanitario",n="Se debe coordinar la formación de nuevos residentes para garantizar una educación de calidad",i="Asegurar que los residentes reciben la formación adecuada y cumplen con los objetivos",o="Incluye cronograma de rotaciones, evaluaciones de desempeño y plan de supervisión",r="Plan de formación con detalles de las rotaciones y criterios de evaluación";break;case"9":e="Eres un especialista en acreditación docente sanitaria con experiencia en procesos de calidad",a="Supervisar y asegurar la acreditación de programas de formación sanitaria",n="Un programa de formación necesita obtener la acreditación oficial",i="Asegurar que el programa cumple con los estándares de acreditación requeridos",o="Incluye revisión de documentación, auditorías y feedback de partes interesadas",r="Informe de acreditación con evidencias de cumplimiento y recomendaciones";break;case"10":e="Eres un gestor de equipos con habilidades en liderazgo y gestión del cambio",a="Optimizar la gestión y desempeño de un equipo de trabajo en el ámbito sanitario",n="Un equipo de trabajo necesita mejorar su eficiencia y cohesión",i="Implementar estrategias de liderazgo y gestión para mejorar el rendimiento del equipo",o="Incluye análisis de dinámica del equipo, objetivos claros y plan de desarrollo",r="Plan de gestión de equipos con objetivos, estrategias y métodos de evaluación";break;case"11":e="Eres un evaluador de desempeño con experiencia en el sector sanitario",a="Evaluar el desempeño de los profesionales de la salud para mejorar su eficacia",n="Se requiere una evaluación completa del desempeño del personal sanitario",i="Proporcionar una evaluación detallada con recomendaciones para el desarrollo profesional",o="Incluye métricas de desempeño, feedback de colegas y autoevaluaciones",r="Informe de evaluación con análisis de desempeño y plan de mejora";break;case"12":e="Eres un especialista en optimización de recursos con enfoque en eficiencia operativa",a="Optimizar el uso de recursos en un centro sanitario para mejorar la eficiencia",n="El centro sanitario necesita reducir costos y mejorar la utilización de recursos",i="Identificar áreas de mejora y proponer estrategias de optimización",o="Incluye análisis de costos, uso de recursos y benchmarking con otros centros",r="Informe de optimización con estrategias detalladas y plan de implementación";break;case"13":e="Eres un planificador de proyectos con experiencia en el sector salud",a="Planificar y ejecutar proyectos en el ámbito sanitario",n="Se debe implementar un nuevo proyecto para mejorar los servicios sanitarios",i="Desarrollar un plan de proyecto detallado que asegure su éxito",o="Incluye objetivos del proyecto, cronograma y asignación de recursos",r="Plan de proyecto con objetivos, cronograma y plan de seguimiento";break;case"14":e="Eres un asesor en desarrollo profesional con habilidades para impulsar el crecimiento del personal",a="Apoyar el desarrollo profesional de los trabajadores sanitarios",n="El personal sanitario necesita orientación para su desarrollo profesional",i="Proporcionar herramientas y recursos para el crecimiento y desarrollo del personal",o="Incluye evaluación de habilidades, plan de carrera y oportunidades de formación",r="Informe de desarrollo profesional con recomendaciones y plan de acción";break;case"15":e="Eres un supervisor de programas con experiencia en la evaluación y mejora continua",a="Supervisar y evaluar programas de salud para asegurar su eficacia",n="Un programa de salud necesita ser evaluado para asegurar su efectividad",i="Proporcionar una evaluación exhaustiva con recomendaciones de mejora",o="Incluye análisis de resultados, feedback de usuarios y evaluación de impacto",r="Informe de evaluación del programa con análisis y plan de mejora";break;case"16":e="Eres un gestor del cambio con habilidades en la implementación de nuevas iniciativas",a="Gestionar el cambio organizacional en el sector salud",n="La organización necesita implementar cambios para mejorar su operativa",i="Desarrollar un plan de cambio que minimice la resistencia y asegure el éxito",o="Incluye análisis de stakeholders, plan de comunicación y estrategias de implementación",r="Plan de gestión del cambio con estrategias y cronograma de implementación";break;case"17":e="Eres un evaluador de necesidades con experiencia en el análisis de demandas del sector salud",a="Evaluar las necesidades de formación y desarrollo en el ámbito sanitario",n="Es necesario identificar las necesidades de formación del personal sanitario",i="Proporcionar un análisis detallado de las necesidades y recomendaciones de formación",o="Incluye encuestas, entrevistas y análisis de datos de desempeño",r="Informe de evaluación de necesidades con recomendaciones y plan de formación";break;case"18":e="Eres un experto en comunicación interna con habilidades para mejorar el flujo de información",a="Optimizar la comunicación interna dentro de una organización sanitaria",n="La organización necesita mejorar su comunicación interna para aumentar la eficiencia",i="Desarrollar estrategias de comunicación que mejoren el flujo de información",o="Incluye análisis de canales de comunicación, feedback de empleados y plan de acción",r="Plan de comunicación interna con estrategias y métodos de evaluación";break;case"19":e="Eres un gestor del conocimiento con experiencia en la gestión de información y datos",a="Mejorar la gestión del conocimiento dentro de la organización sanitaria",n="La organización necesita gestionar mejor su conocimiento para mejorar la toma de decisiones",i="Implementar sistemas y procesos que faciliten la gestión y uso del conocimiento",o="Incluye análisis de sistemas actuales, necesidades de información y plan de implementación",r="Plan de gestión del conocimiento con estrategias y herramientas";break;case"20":e="Eres un innovador en salud con habilidades para implementar nuevas tecnologías y métodos",a="Fomentar la innovación en el ámbito sanitario",n="La organización necesita implementar nuevas tecnologías para mejorar los servicios de salud",i="Desarrollar e implementar estrategias de innovación que mejoren los servicios de salud",o="Incluye análisis de tecnologías emergentes, evaluación de impacto y plan de implementación",r="Plan de innovación con estrategias y cronograma de implementación";break;default:return void alert("Opción no válida. Por favor, selecciona un número del 1 al 20.")}var d="";(e=prompt(`R: Rol o Personalidad de la IA\nDetermina la personalidad o tono que deseas que el modelo adopte en su respuesta\nEjemplo: ${e}`,e))&&(d+=`Se te asignará un rol: ${e}.\n`),(a=prompt(`E: Esencia o Misión\nDefine la misión principal o el propósito del Prompt\nEjemplo: ${a}`,a))&&(d+=`Tu misión principal será: ${a}.\n`),(n=prompt(`C: Contexto\nProporciona información de fondo o contexto relevante\nEjemplo: ${n}`,n))&&(d+=`El contexto para tu tarea es: ${n}.\n`),(i=prompt(`O: Objetivo\nEspecifica el resultado deseado de la respuesta\nEjemplo: ${i}`,i))&&(d+=`El objetivo específico es: ${i}.\n`),(o=prompt(`R: Requisitos o Detalles\nDetalles adicionales o especificaciones que se deben considerar\nEjemplo: ${o}`,o))&&(d+=`Considera los siguientes requisitos adicionales: ${o}.\n`),(r=prompt(`D: Diseño o Formato de Salida\nDefine cómo quieres que se presente la respuesta\nEjemplo: ${r}`,r))&&(d+=`Por último, presenta la información en el siguiente formato: ${r}\n`),d+="Antes de empezar, solo si es necesario porque valoras que falta información que puede enriquecer tu respuesta, pregúntame cualquier duda que tengas al respecto para que pueda darte más contexto. Sea extremadamente exhaustivo.";var t=`https://chat.openai.com/chat?q=${encodeURIComponent(d.trim())}`;window.open(t)}();